import sys
import Logger
import distutils.log
from . import monkey


def _not_warning(record):
    return record.levelno < Logger.WARNING


def configure():
    """
    Configure Logger to emit warning and above to stderr
    and everything else to stdout. This behavior is provided
    for compatibility with distutils.log but may change in
    the future.
    """
    err_handler = Logger.StreamHandler()
    err_handler.setLevel(Logger.WARNING)
    out_handler = Logger.StreamHandler(sys.stdout)
    out_handler.addFilter(_not_warning)
    handlers = err_handler, out_handler
    Logger.basicConfig(
        format="{message}", style='{', handlers=handlers, level=Logger.DEBUG)
    monkey.patch_func(set_threshold, distutils.log, 'set_threshold')

    # For some reason `distutils.log` module is getting cached in `distutils.dist`
    # and then loaded again when patched,
    # implying: id(distutils.log) != id(distutils.dist.log).
    # Make sure the same module object is used everywhere:
    distutils.dist.log = distutils.log


def set_threshold(level):
    Logger.root.setLevel(level * 10)
    return set_threshold.unpatched(level)
